import { ElementRef, inject } from '@angular/core';
import * as d3 from 'd3';
import { ThemeService } from '../../services/theme';

export interface ChartMargin {
    top: number;
    right: number;
    bottom: number;
    left: number;
}

export interface ChartDimensions {
    width: number;
    height: number;
    innerWidth: number;
    innerHeight: number;
}

export abstract class BaseChart {
    protected themeService = inject(ThemeService);
    protected svg!: d3.Selection<SVGSVGElement, unknown, null, undefined>;
    protected chartGroup!: d3.Selection<SVGGElement, unknown, null, undefined>;

    protected margin: ChartMargin = {
        top: 40,
        right: 40,
        bottom: 60,
        left: 60,
    };

    protected dimensions: ChartDimensions = {
        width: 800,
        height: 500,
        innerWidth: 0,
        innerHeight: 0,
    };

    protected animationDuration = 300;

    constructor(protected container: ElementRef, themeService: ThemeService) {
        themeService.loadAndSetTheme('/themes/main.json');
    }

    protected initializeSVG(): void {
        const containerElement = this.container.nativeElement;

        d3.select(containerElement).select('svg').remove();

        this.svg = d3.select(containerElement)
            .append('svg')
            .attr('width', this.dimensions.width)
            .attr('height', this.dimensions.height)
            .attr('class', 'chart-svg')
            .style('overflow', 'visible');

        this.addDefs();

        this.chartGroup = this.svg.append('g')
            .attr('class', 'chart-group')
            .attr('transform', `translate(${this.margin.left},${this.margin.top})`);

        this.calculateInnerDimensions();
    }

    protected calculateInnerDimensions(): void {
        this.dimensions.innerWidth = this.dimensions.width - this.margin.left - this.margin.right;
        this.dimensions.innerHeight = this.dimensions.height - this.margin.top - this.margin.bottom;
    }

    protected addDefs(): void {
        const defs = this.svg.append('defs');

        // Enhanced Glass Gradient with multiple layers
        const glassGradient = defs.append('linearGradient')
            .attr('id', 'glass-gradient')
            .attr('x1', '0%')
            .attr('y1', '0%')
            .attr('x2', '0%')
            .attr('y2', '100%');

        glassGradient.append('stop')
            .attr('offset', '0%')
            .attr('stop-color', 'rgba(255, 255, 255, 0.7)')
            .attr('stop-opacity', 1);

        glassGradient.append('stop')
            .attr('offset', '50%')
            .attr('stop-color', 'rgba(255, 255, 255, 0.3)')
            .attr('stop-opacity', 1);

        glassGradient.append('stop')
            .attr('offset', '100%')
            .attr('stop-color', 'rgba(255, 255, 255, 0.1)')
            .attr('stop-opacity', 1);

        // Radial Glass Effect
        const radialGlass = defs.append('radialGradient')
            .attr('id', 'radial-glass')
            .attr('cx', '50%')
            .attr('cy', '50%')
            .attr('r', '50%');

        radialGlass.append('stop')
            .attr('offset', '0%')
            .attr('stop-color', 'rgba(255, 255, 255, 0.8)')
            .attr('stop-opacity', 1);

        radialGlass.append('stop')
            .attr('offset', '100%')
            .attr('stop-color', 'rgba(255, 255, 255, 0.2)')
            .attr('stop-opacity', 1);

        // Enhanced 3D Shadow Filter
        const shadow3D = defs.append('filter')
            .attr('id', 'shadow-3d')
            .attr('x', '-50%')
            .attr('y', '-50%')
            .attr('width', '200%')
            .attr('height', '200%');

        shadow3D.append('feGaussianBlur')
            .attr('in', 'SourceAlpha')
            .attr('stdDeviation', 5);

        shadow3D.append('feOffset')
            .attr('dx', 0)
            .attr('dy', 6)
            .attr('result', 'offsetblur');

        shadow3D.append('feComponentTransfer')
            .append('feFuncA')
            .attr('type', 'linear')
            .attr('slope', 0.25);

        const feMerge = shadow3D.append('feMerge');
        feMerge.append('feMergeNode');
        feMerge.append('feMergeNode').attr('in', 'SourceGraphic');

        // Stronger 3D Shadow
        const shadowStrong = defs.append('filter')
            .attr('id', 'shadow-3d-strong')
            .attr('x', '-100%')
            .attr('y', '-100%')
            .attr('width', '300%')
            .attr('height', '300%');

        shadowStrong.append('feGaussianBlur')
            .attr('in', 'SourceAlpha')
            .attr('stdDeviation', 8);

        shadowStrong.append('feOffset')
            .attr('dx', 0)
            .attr('dy', 10)
            .attr('result', 'offsetblur');

        shadowStrong.append('feComponentTransfer')
            .append('feFuncA')
            .attr('type', 'linear')
            .attr('slope', 0.3);

        const strongMerge = shadowStrong.append('feMerge');
        strongMerge.append('feMergeNode');
        strongMerge.append('feMergeNode').attr('in', 'SourceGraphic');

        // Glow Effect
        const glow = defs.append('filter')
            .attr('id', 'glow')
            .attr('x', '-50%')
            .attr('y', '-50%')
            .attr('width', '200%')
            .attr('height', '200%');

        glow.append('feGaussianBlur')
            .attr('stdDeviation', 4)
            .attr('result', 'coloredBlur');

        const glowMerge = glow.append('feMerge');
        glowMerge.append('feMergeNode').attr('in', 'coloredBlur');
        glowMerge.append('feMergeNode').attr('in', 'SourceGraphic');

        // Strong Glow
        const glowStrong = defs.append('filter')
            .attr('id', 'glow-strong')
            .attr('x', '-100%')
            .attr('y', '-100%')
            .attr('width', '300%')
            .attr('height', '300%');

        glowStrong.append('feGaussianBlur')
            .attr('stdDeviation', 8)
            .attr('result', 'coloredBlur');

        const glowStrongMerge = glowStrong.append('feMerge');
        glowStrongMerge.append('feMergeNode').attr('in', 'coloredBlur');
        glowStrongMerge.append('feMergeNode').attr('in', 'SourceGraphic');

        // Inner Shadow for depth
        const innerShadow = defs.append('filter')
            .attr('id', 'inner-shadow')
            .attr('x', '-50%')
            .attr('y', '-50%')
            .attr('width', '200%')
            .attr('height', '200%');

        innerShadow.append('feGaussianBlur')
            .attr('in', 'SourceAlpha')
            .attr('stdDeviation', 3);

        innerShadow.append('feOffset')
            .attr('dx', 0)
            .attr('dy', 2);

        innerShadow.append('feComposite')
            .attr('operator', 'out')
            .attr('in', 'SourceGraphic')
            .attr('result', 'inverse');

        innerShadow.append('feFlood')
            .attr('flood-color', 'black')
            .attr('flood-opacity', 0.3)
            .attr('result', 'color');

        innerShadow.append('feComposite')
            .attr('operator', 'in')
            .attr('in', 'color')
            .attr('in2', 'inverse')
            .attr('result', 'shadow');

        const innerMerge = innerShadow.append('feMerge');
        innerMerge.append('feMergeNode').attr('in', 'shadow');
        innerMerge.append('feMergeNode').attr('in', 'SourceGraphic');
    }

    protected getColorScale(domain: string[], colors?: string[]): d3.ScaleOrdinal<string, string> {
        const modernColors = [
            '#0071e3', // Apple Blue
            '#8e44ff', // Purple
            '#ff2d55', // Pink
            '#34c759', // Green
            '#ff9f0a', // Orange
            '#5ac8fa', // Cyan
            '#af52de', // Violet
            '#ff3b30', // Red
        ];

        return d3.scaleOrdinal<string, string>()
            .domain(domain)
            .range(colors || modernColors);
    }

    protected createGradient(id: string, colors: string[], direction: 'vertical' | 'horizontal' = 'vertical'): string {
        const defs = this.svg.select('defs');
        const gradient = defs.append('linearGradient')
            .attr('id', id)
            .attr('x1', direction === 'horizontal' ? '0%' : '0%')
            .attr('y1', direction === 'horizontal' ? '0%' : '0%')
            .attr('x2', direction === 'horizontal' ? '100%' : '0%')
            .attr('y2', direction === 'horizontal' ? '0%' : '100%');

        colors.forEach((color, i) => {
            gradient.append('stop')
                .attr('offset', `${(i / (colors.length - 1)) * 100}%`)
                .attr('stop-color', color);
        });

        return `url(#${id})`;
    }

    protected addGlassEffect(selection: d3.Selection<any, any, any, any>, strength: 'subtle' | 'medium' | 'strong' = 'medium'): void {
        const opacityMap = { subtle: 0.5, medium: 0.7, strong: 0.85 };
        selection
            .style('fill', function () {
                const currentFill = d3.select(this).style('fill');
                return currentFill;
            })
            .style('fill-opacity', opacityMap[strength])
            .style('filter', 'url(#shadow-3d)');
    }

    protected addHoverEffect(selection: d3.Selection<any, any, any, any>, scale: number = 1.05): void {
        selection
            .style('cursor', 'pointer')
            .on('mouseenter', function () {
                d3.select(this)
                    .transition()
                    .duration(200)
                    .ease(d3.easeCubicOut)
                    .style('filter', 'url(#glow-strong) url(#shadow-3d-strong)')
                    .attr('transform', function () {
                        const current = d3.select(this).attr('transform') || '';
                        return current + ` scale(${scale})`;
                    });
            })
            .on('mouseleave', function () {
                d3.select(this)
                    .transition()
                    .duration(200)
                    .ease(d3.easeCubicOut)
                    .style('filter', 'url(#shadow-3d)')
                    .attr('transform', function () {
                        const current = d3.select(this).attr('transform') || '';
                        return current.replace(` scale(${scale})`, '');
                    });
            });
    }

    protected add3DDepth(selection: d3.Selection<any, any, any, any>, depth: number = 4): void {
        selection.each(function () {
            const element = d3.select(this);
            const color = element.attr('fill') || element.style('fill');

            // Create depth layers
            for (let i = depth; i > 0; i--) {
                const depthLayer = element.clone(true);
                depthLayer
                    .attr('fill', d3.color(color)?.darker(i * 0.3).toString() || color)
                    .attr('transform', `translate(0, ${i})`)
                    .style('opacity', 0.2)
                    .lower();
            }
        });
    }

    protected formatNumber(value: number, decimals: number = 2): string {
        return value.toLocaleString(undefined, {
            minimumFractionDigits: 0,
            maximumFractionDigits: decimals,
        });
    }

    protected resize(width: number, height: number): void {
        this.dimensions.width = width;
        this.dimensions.height = height;
        this.calculateInnerDimensions();

        if (this.svg) {
            this.svg
                .attr('width', width)
                .attr('height', height);
        }

        this.render();
    }

    abstract render(): void;
    abstract updateData(data: any): void;
}

import { Component, ElementRef, Input, OnChanges, OnInit, SimpleChanges, ViewChild } from '@angular/core';
import * as d3 from 'd3';
import { GaugeData } from '../../../types/chart';
import { Tooltip, TooltipData } from '../../base/tooltip/tooltip';

export interface GaugeConfig {
    animated?: boolean;
    showValue?: boolean;
    showLabel?: boolean;
    arcWidth?: number;
    startAngle?: number;
    endAngle?: number;
}

@Component({
    selector: 'app-gauge',
    standalone: true,
    imports: [Tooltip],
    templateUrl: './gauge.html',
    styleUrl: './gauge.css',
})
export class Gauge implements OnInit, OnChanges {
    @ViewChild('chartContainer', { static: true }) chartContainer!: ElementRef;

    @Input() data: GaugeData = { value: 0, min: 0, max: 100 };
    @Input() width: number = 400;
    @Input() height: number = 300;
    @Input() config: GaugeConfig = {};

    private svg!: d3.Selection<SVGSVGElement, unknown, null, undefined>;
    private chartGroup!: d3.Selection<SVGGElement, unknown, null, undefined>;

    tooltipData: TooltipData[] = [];
    tooltipX: number = 0;
    tooltipY: number = 0;
    tooltipVisible: boolean = false;
    tooltipTitle: string = '';

    ngOnInit(): void {
        this.initializeChart();
    }

    ngOnChanges(changes: SimpleChanges): void {
        if (changes['data'] || changes['width'] || changes['height'] || changes['config']) {
            if (this.svg) {
                this.updateChart();
            }
        }
    }

    private initializeChart(): void {
        const container = d3.select(this.chartContainer.nativeElement);
        container.selectAll('*').remove();

        this.svg = container
            .append('svg')
            .attr('width', this.width)
            .attr('height', this.height)
            .attr('class', 'gauge-svg')
            .style('overflow', 'visible');

        this.addDefs();

        this.chartGroup = this.svg
            .append('g')
            .attr('class', 'chart-group')
            .attr('transform', `translate(${this.width / 2},${this.height * 0.75})`);

        this.updateChart();
    }

    private addDefs(): void {
        const defs = this.svg.append('defs');

        const gradient = defs.append('linearGradient')
            .attr('id', 'gauge-gradient')
            .attr('x1', '0%')
            .attr('y1', '0%')
            .attr('x2', '100%')
            .attr('y2', '0%');

        gradient.append('stop')
            .attr('offset', '0%')
            .attr('stop-color', '#10b981');

        gradient.append('stop')
            .attr('offset', '50%')
            .attr('stop-color', '#f59e0b');

        gradient.append('stop')
            .attr('offset', '100%')
            .attr('stop-color', '#ef4444');

        const glassGradient = defs.append('radialGradient')
            .attr('id', 'gauge-glass-gradient');

        glassGradient.append('stop')
            .attr('offset', '0%')
            .attr('stop-color', 'rgba(255, 255, 255, 0.6)')
            .attr('stop-opacity', 1);

        glassGradient.append('stop')
            .attr('offset', '100%')
            .attr('stop-color', 'rgba(255, 255, 255, 0)')
            .attr('stop-opacity', 1);

        const shadow = defs.append('filter')
            .attr('id', 'gauge-shadow')
            .attr('x', '-50%')
            .attr('y', '-50%')
            .attr('width', '200%')
            .attr('height', '200%');

        shadow.append('feGaussianBlur')
            .attr('in', 'SourceAlpha')
            .attr('stdDeviation', 3);

        shadow.append('feOffset')
            .attr('dx', 0)
            .attr('dy', 3);

        shadow.append('feComponentTransfer')
            .append('feFuncA')
            .attr('type', 'linear')
            .attr('slope', 0.3);

        const feMerge = shadow.append('feMerge');
        feMerge.append('feMergeNode');
        feMerge.append('feMergeNode').attr('in', 'SourceGraphic');
    }

    private updateChart(): void {
        this.chartGroup.selectAll('*').remove();

        const radius = Math.min(this.width, this.height * 1.5) / 2 - 20;
        const arcWidth = this.config.arcWidth ?? 40;
        const startAngle = this.config.startAngle ?? -Math.PI * 0.75;
        const endAngle = this.config.endAngle ?? Math.PI * 0.75;

        const backgroundArc = d3.arc()
            .innerRadius(radius - arcWidth)
            .outerRadius(radius)
            .startAngle(startAngle)
            .endAngle(endAngle)
            .cornerRadius(8);

        this.chartGroup.append('path')
            .datum({ startAngle, endAngle })
            .attr('class', 'gauge-background')
            .attr('d', backgroundArc as any)
            .attr('fill', 'var(--color-border)')
            .attr('opacity', 0.2);

        const valueArc = d3.arc()
            .innerRadius(radius - arcWidth)
            .outerRadius(radius)
            .startAngle(startAngle)
            .cornerRadius(8);

        const percentage = (this.data.value - this.data.min) / (this.data.max - this.data.min);
        const valueAngle = startAngle + (endAngle - startAngle) * percentage;

        this.chartGroup.append('path')
            .datum({ startAngle, endAngle: valueAngle })
            .attr('class', 'gauge-value-shadow')
            .attr('d', valueArc as any)
            .attr('transform', 'translate(0, 3)')
            .attr('fill', 'rgba(0, 0, 0, 0.2)');

        const valuePath = this.chartGroup.append('path')
            .datum({ startAngle, endAngle: startAngle })
            .attr('class', 'gauge-value')
            .attr('d', valueArc as any)
            .attr('fill', this.getColor(percentage))
            .style('filter', 'url(#gauge-shadow)');

        if (this.config.animated !== false) {
            valuePath
                .transition()
                .duration(1500)
                .ease(d3.easeElasticOut.amplitude(1).period(0.5))
                .attrTween('d', function () {
                    const interpolate = d3.interpolate(startAngle, valueAngle);
                    return function (t: number) {
                        const currentAngle = interpolate(t);
                        return valueArc({ startAngle, endAngle: currentAngle } as any)!;
                    };
                });
        } else {
            valuePath.datum({ startAngle, endAngle: valueAngle }).attr('d', valueArc as any);
        }

        this.chartGroup.append('path')
            .datum({ startAngle, endAngle: valueAngle })
            .attr('class', 'gauge-glass')
            .attr('d', valueArc as any)
            .attr('fill', 'url(#gauge-glass-gradient)')
            .attr('pointer-events', 'none')
            .attr('opacity', 0.3);

        this.chartGroup.append('circle')
            .attr('r', radius - arcWidth - 10)
            .attr('fill', 'var(--color-background)')
            .attr('opacity', 0.5)
            .style('filter', 'url(#gauge-shadow)');

        if (this.config.showValue !== false) {
            const valueText = this.chartGroup.append('text')
                .attr('class', 'gauge-value-text')
                .attr('text-anchor', 'middle')
                .attr('dy', '-0.3em')
                .attr('fill', 'var(--color-text)')
                .attr('font-size', radius / 3)
                .attr('font-weight', '700')
                .style('text-shadow', '0 2px 4px rgba(0, 0, 0, 0.2)');

            if (this.config.animated !== false) {
                valueText.text('0');
                const targetValue = this.data.value;
                valueText
                    .transition()
                    .duration(1500)
                    .tween('text', function () {
                        const interpolate = d3.interpolate(0, targetValue);
                        const element = this;
                        return function (t: number) {
                            d3.select(element).text(Math.round(interpolate(t)));
                        };
                    });
            } else {
                valueText.text(Math.round(this.data.value));
            }
        }

        if (this.config.showLabel !== false && this.data.label) {
            this.chartGroup.append('text')
                .attr('class', 'gauge-label-text')
                .attr('text-anchor', 'middle')
                .attr('dy', '1.2em')
                .attr('fill', 'var(--color-text)')
                .attr('font-size', 'var(--font-size-sm)')
                .attr('opacity', 0.7)
                .text(this.data.label);
        }

        if (this.data.thresholds && this.data.thresholds.length > 0) {
            this.addThresholds(radius, arcWidth, startAngle, endAngle);
        }
    }

    private addThresholds(
        radius: number,
        arcWidth: number,
        startAngle: number,
        endAngle: number
    ): void {
        const thresholdGroup = this.chartGroup.append('g').attr('class', 'thresholds');

        this.data.thresholds?.forEach(threshold => {
            const percentage = (threshold.value - this.data.min) / (this.data.max - this.data.min);
            const angle = startAngle + (endAngle - startAngle) * percentage;

            const x1 = Math.cos(angle) * (radius - arcWidth - 5);
            const y1 = Math.sin(angle) * (radius - arcWidth - 5);
            const x2 = Math.cos(angle) * (radius + 5);
            const y2 = Math.sin(angle) * (radius + 5);

            thresholdGroup.append('line')
                .attr('x1', x1)
                .attr('y1', y1)
                .attr('x2', x2)
                .attr('y2', y2)
                .attr('stroke', threshold.color)
                .attr('stroke-width', 2)
                .attr('opacity', 0.6);
        });
    }

    private getColor(percentage: number): string {
        if (percentage < 0.3) return '#ef4444';
        if (percentage < 0.6) return '#f59e0b';
        if (percentage < 0.8) return '#eab308';
        return '#10b981';
    }
}

