import { ChangeDetectorRef, Component, ElementRef, Input, NgZone, OnChanges, OnInit, SimpleChanges, ViewChild } from '@angular/core';
import * as d3 from 'd3';
import { ThemeService } from '../../../services/theme';
import { TimeSeriesDataPoint } from '../../../types/chart';
import { Tooltip, TooltipData } from '../../base/tooltip/tooltip';

export interface AreaChartConfig {
    showGrid?: boolean;
    showLine?: boolean;
    showPoints?: boolean;
    curved?: boolean;
    animated?: boolean;
    stacked?: boolean;
    colors?: string[];
    fillOpacity?: number;
    customTooltip?: (data: { date: Date; value: number; seriesName?: string; }) => { title?: string; items: Array<{ label: string; value: string | number; color?: string; }>; };
}

@Component({
    selector: 'app-area',
    standalone: true,
    imports: [Tooltip],
    templateUrl: './area.html',
    styleUrl: './area.css',
})
export class Area implements OnInit, OnChanges {

    constructor(private ngZone: NgZone, private cdr: ChangeDetectorRef, themeService: ThemeService) {
        themeService.loadAndSetTheme('/themes/main.json');
    }

    @ViewChild('chartContainer', { static: true }) chartContainer!: ElementRef;

    @Input() data: TimeSeriesDataPoint[] | Record<string, TimeSeriesDataPoint[]> = [];
    @Input() width: number = 800;
    @Input() height: number = 500;
    @Input() config: AreaChartConfig = {};

    private svg!: d3.Selection<SVGSVGElement, unknown, null, undefined>;
    private chartGroup!: d3.Selection<SVGGElement, unknown, null, undefined>;
    private margin = { top: 40, right: 40, bottom: 60, left: 60 };
    private innerWidth!: number;

    tooltipData: TooltipData[] = [];
    tooltipX: number = 0;
    tooltipY: number = 0;
    tooltipVisible: boolean = false;
    tooltipTitle: string = '';
    private innerHeight!: number;

    ngOnInit(): void {
        this.initializeChart();
    }

    ngOnChanges(changes: SimpleChanges): void {
        if (changes['data'] || changes['width'] || changes['height'] || changes['config']) {
            if (this.svg) {
                this.updateChart();
            }
        }
    }

    private initializeChart(): void {
        this.innerWidth = this.width - this.margin.left - this.margin.right;
        this.innerHeight = this.height - this.margin.top - this.margin.bottom;

        const container = d3.select(this.chartContainer.nativeElement);
        container.selectAll('*').remove();

        this.svg = container
            .append('svg')
            .attr('width', this.width)
            .attr('height', this.height)
            .attr('class', 'area-chart-svg')
            .style('overflow', 'visible');

        this.addDefs();

        this.chartGroup = this.svg
            .append('g')
            .attr('class', 'chart-group')
            .attr('transform', `translate(${this.margin.left},${this.margin.top})`);

        this.updateChart();
    }

    private addDefs(): void {
        const defs = this.svg.append('defs');

        const colors = this.config.colors || ['#3b82f6', '#8b5cf6', '#ec4899', '#10b981', '#f59e0b', '#ef4444'];

        colors.forEach((color, i) => {
            const gradient = defs.append('linearGradient')
                .attr('id', `area-gradient-${i}`)
                .attr('x1', '0%')
                .attr('y1', '0%')
                .attr('x2', '0%')
                .attr('y2', '100%');

            gradient.append('stop')
                .attr('offset', '0%')
                .attr('stop-color', color)
                .attr('stop-opacity', this.config.fillOpacity ?? 0.6);

            gradient.append('stop')
                .attr('offset', '100%')
                .attr('stop-color', color)
                .attr('stop-opacity', 0.1);
        });

        const glow = defs.append('filter')
            .attr('id', 'area-glow')
            .attr('x', '-50%')
            .attr('y', '-50%')
            .attr('width', '200%')
            .attr('height', '200%');

        glow.append('feGaussianBlur')
            .attr('stdDeviation', 2)
            .attr('result', 'coloredBlur');

        const glowMerge = glow.append('feMerge');
        glowMerge.append('feMergeNode').attr('in', 'coloredBlur');
        glowMerge.append('feMergeNode').attr('in', 'SourceGraphic');
    }

    private updateChart(): void {
        if (!this.data || (Array.isArray(this.data) && this.data.length === 0)) return;

        this.chartGroup.selectAll('*').remove();

        const isMultiSeries = !Array.isArray(this.data);
        const seriesData: Record<string, TimeSeriesDataPoint[]> = isMultiSeries
            ? this.data as Record<string, TimeSeriesDataPoint[]>
            : { 'Series 1': this.data as TimeSeriesDataPoint[] };

        const allData = Object.values(seriesData).flat();

        const xScale = d3.scaleTime()
            .domain(d3.extent(allData, d => d.date) as [Date, Date])
            .range([0, this.innerWidth]);

        const yScale = d3.scaleLinear()
            .domain([0, d3.max(allData, d => d.value) || 0])
            .nice()
            .range([this.innerHeight, 0]);

        const colorScale = d3.scaleOrdinal<string>()
            .domain(Object.keys(seriesData))
            .range(this.config.colors || ['#3b82f6', '#8b5cf6', '#ec4899', '#10b981', '#f59e0b', '#ef4444']);

        if (this.config.showGrid !== false) {
            this.addGrid(xScale, yScale);
        }

        Object.entries(seriesData).forEach(([seriesName, data], index) => {
            this.addArea(data, xScale, yScale, colorScale(seriesName), index, seriesName);
        });

        this.addAxes(xScale, yScale);

        // Add overlay for hover interaction
        this.addHoverOverlay(seriesData, xScale, yScale, colorScale);
    }

    private addGrid(
        xScale: d3.ScaleTime<number, number>,
        yScale: d3.ScaleLinear<number, number>
    ): void {
        const gridGroup = this.chartGroup.append('g').attr('class', 'grid');

        gridGroup.selectAll('.grid-line-y')
            .data(yScale.ticks(5))
            .join('line')
            .attr('class', 'grid-line-y')
            .attr('x1', 0)
            .attr('x2', this.innerWidth)
            .attr('y1', d => yScale(d))
            .attr('y2', d => yScale(d))
            .attr('stroke', 'var(--color-border)')
            .attr('stroke-width', 1)
            .attr('stroke-dasharray', '4,4')
            .attr('opacity', 0.2);

        gridGroup.selectAll('.grid-line-x')
            .data(xScale.ticks(5))
            .join('line')
            .attr('class', 'grid-line-x')
            .attr('x1', d => xScale(d))
            .attr('x2', d => xScale(d))
            .attr('y1', 0)
            .attr('y2', this.innerHeight)
            .attr('stroke', 'var(--color-border)')
            .attr('stroke-width', 1)
            .attr('stroke-dasharray', '4,4')
            .attr('opacity', 0.2);
    }

    private addAxes(
        xScale: d3.ScaleTime<number, number>,
        yScale: d3.ScaleLinear<number, number>
    ): void {

        this.chartGroup.append('g')
            .attr('class', 'x-axis')
            .attr('transform', `translate(0,${this.innerHeight})`)
            .call(d3.axisBottom(xScale).ticks(5))
            .selectAll('text')
            .attr('fill', 'var(--color-text)')
            .attr('font-size', 'var(--font-size-sm)');

        this.chartGroup.append('g')
            .attr('class', 'y-axis')
            .call(d3.axisLeft(yScale).ticks(5))
            .selectAll('text')
            .attr('fill', 'var(--color-text)')
            .attr('font-size', 'var(--font-size-sm)');

        this.chartGroup.selectAll('.domain')
            .attr('stroke', 'var(--color-border)')
            .attr('stroke-width', 2)
            .attr('opacity', 0.6);

        this.chartGroup.selectAll('.tick line')
            .attr('stroke', 'var(--color-border)')
            .attr('opacity', 0.5);
    }

    private addArea(
        data: TimeSeriesDataPoint[],
        xScale: d3.ScaleTime<number, number>,
        yScale: d3.ScaleLinear<number, number>,
        color: string,
        index: number,
        seriesName: string
    ): void {
        const areaGroup = this.chartGroup.append('g')
            .attr('class', `area-group-${index}`);

        // Define area generator
        const areaGenerator = this.config.curved !== false
            ? d3.area<TimeSeriesDataPoint>()
                .x(d => xScale(d.date))
                .y0(this.innerHeight)
                .y1(d => yScale(d.value))
                .curve(d3.curveMonotoneX)
            : d3.area<TimeSeriesDataPoint>()
                .x(d => xScale(d.date))
                .y0(this.innerHeight)
                .y1(d => yScale(d.value));

        // Add area
        const areaPath = areaGroup.append('path')
            .datum(data)
            .attr('class', 'area-path')
            .attr('d', areaGenerator)
            .attr('fill', `url(#area-gradient-${index})`)
            .attr('opacity', 0);

        if (this.config.animated !== false) {
            areaPath
                .transition()
                .duration(1000)
                .delay(index * 200)
                .attr('opacity', 1);
        } else {
            areaPath.attr('opacity', 1);
        }

        // Add line on top if enabled
        if (this.config.showLine !== false) {
            const lineGenerator = this.config.curved !== false
                ? d3.line<TimeSeriesDataPoint>()
                    .x(d => xScale(d.date))
                    .y(d => yScale(d.value))
                    .curve(d3.curveMonotoneX)
                : d3.line<TimeSeriesDataPoint>()
                    .x(d => xScale(d.date))
                    .y(d => yScale(d.value));

            const linePath = areaGroup.append('path')
                .datum(data)
                .attr('class', 'area-line')
                .attr('d', lineGenerator)
                .attr('fill', 'none')
                .attr('stroke', color)
                .attr('stroke-width', 2.5)
                .attr('stroke-linecap', 'round')
                .attr('stroke-linejoin', 'round')
                .style('filter', 'url(#area-glow)');

            if (this.config.animated !== false) {
                const totalLength = (linePath.node() as any)?.getTotalLength() || 0;
                linePath
                    .attr('stroke-dasharray', `${totalLength} ${totalLength}`)
                    .attr('stroke-dashoffset', totalLength)
                    .transition()
                    .duration(1000)
                    .delay(index * 200)
                    .attr('stroke-dashoffset', 0);
            }
        }

        // Add points if enabled
        if (this.config.showPoints) {
            const points = areaGroup.selectAll('.area-point')
                .data(data)
                .join('circle')
                .attr('class', 'area-point')
                .attr('cx', d => xScale(d.date))
                .attr('cy', d => yScale(d.value))
                .attr('r', 0)
                .attr('fill', color)
                .attr('stroke', '#fff')
                .attr('stroke-width', 2)
                .style('filter', 'drop-shadow(0 2px 4px rgba(0, 0, 0, 0.2))')
                .transition()
                .duration(this.config.animated !== false ? 400 : 0)
                .delay((_, i) => (this.config.animated !== false ? index * 200 + 800 + i * 20 : 0))
                .attr('r', 4);

            // Add hover effects
            areaGroup.selectAll('.area-point')
                .on('mouseenter', (event, d: any) => {
                    d3.select(event.currentTarget)
                        .transition()
                        .duration(200)
                        .attr('r', 6)
                        .attr('stroke-width', 3);

                    this.ngZone.run(() => {
                        this.showTooltip(event, d, seriesName, color);
                    });
                })
                .on('mouseleave', (event) => {
                    d3.select(event.currentTarget)
                        .transition()
                        .duration(200)
                        .attr('r', 4)
                        .attr('stroke-width', 2);

                    this.ngZone.run(() => {
                        this.hideTooltip();
                    });
                });
        }
    }

    private sanitizeClassName(name: string): string {
        return name.replace(/[^a-zA-Z0-9-_]/g, '-');
    }

    private addHoverOverlay(
        seriesData: Record<string, TimeSeriesDataPoint[]>,
        xScale: d3.ScaleTime<number, number>,
        yScale: d3.ScaleLinear<number, number>,
        colorScale: d3.ScaleOrdinal<string, string>
    ): void {
        const overlay = this.chartGroup
            .append('rect')
            .attr('class', 'hover-overlay')
            .attr('width', this.innerWidth)
            .attr('height', this.innerHeight)
            .attr('fill', 'none')
            .attr('pointer-events', 'all')
            .style('cursor', 'crosshair');

        // Create vertical line for hover
        const hoverLine = this.chartGroup
            .append('line')
            .attr('class', 'hover-line')
            .attr('stroke', 'var(--color-border)')
            .attr('stroke-width', 1)
            .attr('stroke-dasharray', '4,4')
            .attr('opacity', 0)
            .attr('pointer-events', 'none');

        // Create hover circles for each series
        const hoverCircles = this.chartGroup
            .append('g')
            .attr('class', 'hover-circles')
            .attr('pointer-events', 'none');

        Object.entries(seriesData).forEach(([seriesName, data]) => {
            hoverCircles
                .append('circle')
                .attr('class', `hover-circle-${this.sanitizeClassName(seriesName)}`)
                .attr('r', 5)
                .attr('fill', colorScale(seriesName))
                .attr('stroke', '#fff')
                .attr('stroke-width', 2)
                .attr('opacity', 0)
                .style('filter', 'drop-shadow(0 2px 4px rgba(0, 0, 0, 0.3))');
        });

        overlay
            .on('mousemove', (event) => {
                const [mouseX] = d3.pointer(event);
                const hoveredDate = xScale.invert(mouseX);

                // Find closest data points for each series
                const closestPoints: Array<{ seriesName: string; point: TimeSeriesDataPoint; distance: number; }> = [];

                Object.entries(seriesData).forEach(([seriesName, data]) => {
                    const bisect = d3.bisector<TimeSeriesDataPoint, Date>(d => d.date).left;
                    const index = bisect(data, hoveredDate);

                    const d0 = data[index - 1];
                    const d1 = data[index];

                    let closest = d0;
                    if (d1 && d0) {
                        closest = hoveredDate.getTime() - d0.date.getTime() > d1.date.getTime() - hoveredDate.getTime() ? d1 : d0;
                    } else if (d1) {
                        closest = d1;
                    }

                    if (closest) {
                        const distance = Math.abs(xScale(closest.date) - mouseX);
                        closestPoints.push({ seriesName, point: closest, distance });
                    }
                });

                if (closestPoints.length > 0) {
                    // Sort by distance and take the closest one for positioning
                    closestPoints.sort((a, b) => a.distance - b.distance);
                    const mainPoint = closestPoints[0];

                    // Show vertical line
                    hoverLine
                        .attr('x1', xScale(mainPoint.point.date))
                        .attr('x2', xScale(mainPoint.point.date))
                        .attr('y1', 0)
                        .attr('y2', this.innerHeight)
                        .attr('opacity', 0.6);

                    // Show circles for all series
                    closestPoints.forEach(({ seriesName, point }) => {
                        hoverCircles
                            .select(`.hover-circle-${this.sanitizeClassName(seriesName)}`)
                            .attr('cx', xScale(point.date))
                            .attr('cy', yScale(point.value))
                            .attr('opacity', 1);
                    });

                    // Show tooltip with all series data
                    this.ngZone.run(() => {
                        this.showMultiSeriesTooltip(event, closestPoints, colorScale);
                    });
                }
            })
            .on('mouseleave', () => {
                hoverLine.attr('opacity', 0);
                hoverCircles.selectAll('circle').attr('opacity', 0);
                this.ngZone.run(() => {
                    this.hideTooltip();
                });
            });
    }

    private showTooltip(event: MouseEvent, dataPoint: TimeSeriesDataPoint, seriesName: string, color: string): void {
        if (this.config.customTooltip) {
            const customData = this.config.customTooltip({ date: dataPoint.date, value: dataPoint.value, seriesName });
            this.tooltipTitle = customData.title || '';
            this.tooltipData = customData.items.map(item => ({
                label: item.label,
                value: item.value,
                color: item.color
            }));
        } else {
            this.tooltipTitle = seriesName;
            this.tooltipData = [
                { label: 'Date', value: dataPoint.date.toLocaleDateString() },
                { label: 'Value', value: dataPoint.value.toFixed(2), color }
            ];
        }

        this.tooltipX = event.clientX;
        this.tooltipY = event.clientY;
        this.tooltipVisible = true;
        this.cdr.detectChanges();
    }

    private showMultiSeriesTooltip(
        event: MouseEvent,
        points: Array<{ seriesName: string; point: TimeSeriesDataPoint; distance: number; }>,
        colorScale: d3.ScaleOrdinal<string, string>
    ): void {
        if (this.config.customTooltip && points.length > 0) {
            const customData = this.config.customTooltip({
                date: points[0].point.date,
                value: points[0].point.value,
                seriesName: points[0].seriesName
            });
            this.tooltipTitle = customData.title || '';
            this.tooltipData = customData.items.map(item => ({
                label: item.label,
                value: item.value,
                color: item.color
            }));
        } else {
            this.tooltipTitle = points[0].point.date.toLocaleDateString();
            this.tooltipData = points.map(({ seriesName, point }) => ({
                label: seriesName,
                value: point.value.toFixed(2),
                color: colorScale(seriesName)
            }));
        }

        this.tooltipX = event.clientX;
        this.tooltipY = event.clientY;
        this.tooltipVisible = true;
        this.cdr.detectChanges();
    }

    private hideTooltip(): void {
        this.tooltipVisible = false;
        this.cdr.detectChanges();
    }
}

import { AfterViewInit, Component, ElementRef, Input, OnChanges, OnInit, SimpleChanges, ViewChild } from '@angular/core';
import * as d3 from 'd3';
import { ThemeService } from '../../../services/theme';

export type AxisOrientation = 'top' | 'bottom' | 'left' | 'right';
export type AxisScale = d3.AxisScale<any>;

@Component({
  selector: 'app-axis',
  standalone: true,
  imports: [],
  templateUrl: './axis.html',
  styleUrl: './axis.css',
})
export class Axis implements OnInit, AfterViewInit, OnChanges {

  constructor(themeService: ThemeService) {
    themeService.loadAndSetTheme('/themes/main.json');
  }

  @ViewChild('axisContainer', { static: false }) axisContainer!: ElementRef<SVGGElement>;

  @Input() scale!: AxisScale;
  @Input() orientation: AxisOrientation = 'bottom';
  @Input() label?: string;
  @Input() ticks?: number;
  @Input() tickFormat?: (domainValue: any, index: number) => string;
  @Input() tickValues?: any[];
  @Input() gridLines: boolean = false;
  @Input() gridWidth?: number;
  @Input() gridHeight?: number;

  private axis!: d3.Axis<any>;

  ngOnInit(): void {
    this.createAxis();
  }

  ngAfterViewInit(): void {
    this.renderAxis();
  }

  ngOnChanges(changes: SimpleChanges): void {
    if (changes['scale'] || changes['orientation'] || changes['ticks'] || changes['tickFormat'] || changes['tickValues']) {
      this.createAxis();
      if (this.axisContainer) {
        this.renderAxis();
      }
    }
  }

  private createAxis(): void {
    switch (this.orientation) {
      case 'top':
        this.axis = d3.axisTop(this.scale);
        break;
      case 'bottom':
        this.axis = d3.axisBottom(this.scale);
        break;
      case 'left':
        this.axis = d3.axisLeft(this.scale);
        break;
      case 'right':
        this.axis = d3.axisRight(this.scale);
        break;
    }

    if (this.ticks) {
      this.axis.ticks(this.ticks);
    }

    if (this.tickFormat) {
      this.axis.tickFormat(this.tickFormat);
    }

    if (this.tickValues) {
      this.axis.tickValues(this.tickValues);
    }
  }

  private renderAxis(): void {
    if (!this.axisContainer) return;

    const g = d3.select(this.axisContainer.nativeElement);
    g.selectAll('*').remove();

    if (this.gridLines && this.gridWidth && this.gridHeight) {
      this.renderGridLines(g);
    }

    g.call(this.axis as any);

    g.selectAll('.domain')
      .attr('stroke', 'var(--color-border)')
      .attr('stroke-width', 2)
      .attr('opacity', 0.6);

    g.selectAll('.tick line')
      .attr('stroke', 'var(--color-border)')
      .attr('stroke-width', 1.5)
      .attr('opacity', 0.5);

    g.selectAll('.tick text')
      .attr('fill', 'var(--color-text)')
      .attr('font-size', 'var(--font-size-sm)')
      .attr('font-family', 'var(--font-family)')
      .style('text-shadow', '0 1px 2px rgba(0, 0, 0, 0.1)');

    if (this.label) {
      this.addAxisLabel(g);
    }
  }

  private renderGridLines(g: d3.Selection<SVGGElement, unknown, null, undefined>): void {
    const isVertical = this.orientation === 'left' || this.orientation === 'right';

    const tickValues = this.tickValues ||
      ((this.scale as any).ticks ? (this.scale as any).ticks(this.ticks || 10) : this.scale.domain());

    if (isVertical) {
      g.selectAll('.grid-line')
        .data(tickValues)
        .join('line')
        .attr('class', 'grid-line')
        .attr('x1', 0)
        .attr('x2', this.gridWidth!)
        .attr('y1', (d: any) => this.scale(d)!)
        .attr('y2', (d: any) => this.scale(d)!)
        .attr('stroke', 'var(--color-border)')
        .attr('stroke-width', 1)
        .attr('opacity', 0.2)
        .attr('stroke-dasharray', '4,4');
    } else {
      g.selectAll('.grid-line')
        .data(tickValues)
        .join('line')
        .attr('class', 'grid-line')
        .attr('x1', (d: any) => this.scale(d)!)
        .attr('x2', (d: any) => this.scale(d)!)
        .attr('y1', 0)
        .attr('y2', -this.gridHeight!)
        .attr('stroke', 'var(--color-border)')
        .attr('stroke-width', 1)
        .attr('opacity', 0.2)
        .attr('stroke-dasharray', '4,4');
    }
  }

  private addAxisLabel(g: d3.Selection<SVGGElement, unknown, null, undefined>): void {
    const isVertical = this.orientation === 'left' || this.orientation === 'right';

    if (isVertical) {
      const yPos = this.gridHeight ? -this.gridHeight / 2 : 0;
      const xPos = this.orientation === 'left' ? -50 : 50;

      g.append('text')
        .attr('class', 'axis-label')
        .attr('transform', `translate(${xPos}, ${yPos}) rotate(-90)`)
        .attr('text-anchor', 'middle')
        .attr('fill', 'var(--color-text)')
        .attr('font-size', 'var(--font-size-md)')
        .attr('font-weight', '600')
        .attr('font-family', 'var(--font-family)')
        .attr('letter-spacing', '0.5px')
        .style('text-shadow', '0 2px 4px rgba(0, 0, 0, 0.15)')
        .text(this.label!);
    } else {
      const xPos = this.gridWidth ? this.gridWidth / 2 : 0;
      const yPos = this.orientation === 'top' ? -25 : 45;

      g.append('text')
        .attr('class', 'axis-label')
        .attr('x', xPos)
        .attr('y', yPos)
        .attr('text-anchor', 'middle')
        .attr('fill', 'var(--color-text)')
        .attr('font-size', 'var(--font-size-md)')
        .attr('font-weight', '600')
        .attr('font-family', 'var(--font-family)')
        .attr('letter-spacing', '0.5px')
        .style('text-shadow', '0 2px 4px rgba(0, 0, 0, 0.15)')
        .text(this.label!);
    }
  }
}

import { ChangeDetectorRef, Component, ElementRef, Input, NgZone, OnChanges, OnInit, SimpleChanges, ViewChild } from '@angular/core';
import * as d3 from 'd3';
import { ThemeService } from '../../../services/theme';
import { ChartDataPoint } from '../../../types/chart';
import { calculateLabelRotation, getMaxLabelWidth } from '../../../utils/chart-helpers';
import { Tooltip, TooltipData } from '../../base/tooltip/tooltip';

export interface BarChartConfig {
  orientation?: 'vertical' | 'horizontal';
  showGrid?: boolean;
  showValues?: boolean;
  animated?: boolean;
  barRadius?: number;
  barPadding?: number;
  colors?: string[];
  customTooltip?: (data: { label: string; value: number; color?: string; }) => { title?: string; items: Array<{ label: string; value: string | number; color?: string; }>; };
}

@Component({
  selector: 'app-bar',
  standalone: true,
  imports: [Tooltip],
  templateUrl: './bar.html',
  styleUrl: './bar.css',
})
export class Bar implements OnInit, OnChanges {
  @ViewChild('chartContainer', { static: true }) chartContainer!: ElementRef;

  constructor(private ngZone: NgZone, private cdr: ChangeDetectorRef, themeService: ThemeService) {
    themeService.loadAndSetTheme('/themes/main.json');
  }

  @Input() data: ChartDataPoint[] = [];
  @Input() width: number = 800;
  @Input() height: number = 500;
  @Input() config: BarChartConfig = {};
  @Input() autoRotateLabels: boolean = true;
  @Input() labelRotationAngle?: number;

  private svg!: d3.Selection<SVGSVGElement, unknown, null, undefined>;
  private chartGroup!: d3.Selection<SVGGElement, unknown, null, undefined>;
  private margin = { top: 40, right: 40, bottom: 60, left: 60 };
  private innerWidth!: number;
  private innerHeight!: number;

  tooltipData: TooltipData[] = [];
  tooltipX: number = 0;
  tooltipY: number = 0;
  tooltipVisible: boolean = false;
  tooltipTitle: string = '';

  ngOnInit(): void {
    this.initializeChart();
  }

  ngOnChanges(changes: SimpleChanges): void {
    if (changes['data'] || changes['width'] || changes['height'] || changes['config']) {
      if (this.svg) {
        this.updateChart();
      }
    }
  }

  private initializeChart(): void {
    this.innerWidth = this.width - this.margin.left - this.margin.right;
    this.innerHeight = this.height - this.margin.top - this.margin.bottom;

    const container = d3.select(this.chartContainer.nativeElement);
    container.selectAll('*').remove();

    this.svg = container
      .append('svg')
      .attr('width', this.width)
      .attr('height', this.height)
      .attr('class', 'bar-chart-svg')
      .style('overflow', 'visible');

    this.addDefs();

    this.chartGroup = this.svg
      .append('g')
      .attr('class', 'chart-group')
      .attr('transform', `translate(${this.margin.left},${this.margin.top})`);

    this.updateChart();
  }

  private addDefs(): void {
    const defs = this.svg.append('defs');

    // Enhanced glass gradient with more layers
    const glassGradient = defs.append('linearGradient')
      .attr('id', 'bar-glass-gradient')
      .attr('x1', '0%')
      .attr('y1', '0%')
      .attr('x2', '0%')
      .attr('y2', '100%');

    glassGradient.append('stop')
      .attr('offset', '0%')
      .attr('stop-color', 'rgba(255, 255, 255, 0.8)')
      .attr('stop-opacity', 1);

    glassGradient.append('stop')
      .attr('offset', '30%')
      .attr('stop-color', 'rgba(255, 255, 255, 0.4)')
      .attr('stop-opacity', 1);

    glassGradient.append('stop')
      .attr('offset', '70%')
      .attr('stop-color', 'rgba(255, 255, 255, 0.1)')
      .attr('stop-opacity', 1);

    glassGradient.append('stop')
      .attr('offset', '100%')
      .attr('stop-color', 'rgba(255, 255, 255, 0)')
      .attr('stop-opacity', 1);

    // Enhanced 3D shadow with multiple layers
    const shadow = defs.append('filter')
      .attr('id', 'bar-3d-shadow')
      .attr('x', '-100%')
      .attr('y', '-100%')
      .attr('width', '300%')
      .attr('height', '300%');

    shadow.append('feGaussianBlur')
      .attr('in', 'SourceAlpha')
      .attr('stdDeviation', 6);

    shadow.append('feOffset')
      .attr('dx', 0)
      .attr('dy', 8)
      .attr('result', 'offsetblur');

    shadow.append('feComponentTransfer')
      .append('feFuncA')
      .attr('type', 'linear')
      .attr('slope', 0.25);

    const feMerge = shadow.append('feMerge');
    feMerge.append('feMergeNode');
    feMerge.append('feMergeNode').attr('in', 'SourceGraphic');

    // Hover glow effect
    const hoverGlow = defs.append('filter')
      .attr('id', 'bar-hover-glow')
      .attr('x', '-100%')
      .attr('y', '-100%')
      .attr('width', '300%')
      .attr('height', '300%');

    hoverGlow.append('feGaussianBlur')
      .attr('stdDeviation', 8)
      .attr('result', 'coloredBlur');

    const glowMerge = hoverGlow.append('feMerge');
    glowMerge.append('feMergeNode').attr('in', 'coloredBlur');
    glowMerge.append('feMergeNode').attr('in', 'SourceGraphic');
  }

  private updateChart(): void {
    if (!this.data || this.data.length === 0) return;

    this.chartGroup.selectAll('*').remove();

    const isVertical = this.config.orientation !== 'horizontal';
    const barRadius = this.config.barRadius ?? 8;

    const xScale = isVertical
      ? d3.scaleBand()
        .domain(this.data.map(d => d.label))
        .range([0, this.innerWidth])
        .padding(this.config.barPadding ?? 0.3)
      : d3.scaleLinear()
        .domain([0, d3.max(this.data, d => d.value) || 0])
        .range([0, this.innerWidth]);

    const yScale = isVertical
      ? d3.scaleLinear()
        .domain([0, d3.max(this.data, d => d.value) || 0])
        .range([this.innerHeight, 0])
      : d3.scaleBand()
        .domain(this.data.map(d => d.label))
        .range([0, this.innerHeight])
        .padding(this.config.barPadding ?? 0.3);

    // Modern 2026 color palette inspired by Apple design
    const modernColors = this.config.colors || [
      '#0071e3', // Apple Blue
      '#8e44ff', // Purple
      '#ff2d55', // Pink
      '#34c759', // Green
      '#ff9f0a', // Orange
      '#5ac8fa', // Cyan
      '#af52de', // Violet
      '#ff3b30'  // Red
    ];

    const colorScale = d3.scaleOrdinal<string>()
      .domain(this.data.map(d => d.label))
      .range(modernColors);

    if (this.config.showGrid !== false) {
      this.addGrid(isVertical ? yScale as any : xScale as any, isVertical);
    }

    this.addAxes(xScale as any, yScale as any, isVertical);

    this.addBars(xScale as any, yScale as any, colorScale, isVertical, barRadius);
  }

  private addGrid(scale: d3.ScaleLinear<number, number>, isVertical: boolean): void {
    const gridGroup = this.chartGroup.append('g').attr('class', 'grid');

    if (isVertical) {
      gridGroup.selectAll('.grid-line')
        .data(scale.ticks(5))
        .join('line')
        .attr('class', 'grid-line')
        .attr('x1', 0)
        .attr('x2', this.innerWidth)
        .attr('y1', d => scale(d))
        .attr('y2', d => scale(d))
        .attr('stroke', 'var(--color-border)')
        .attr('stroke-width', 1)
        .attr('stroke-dasharray', '4,4')
        .attr('opacity', 0.2);
    } else {
      gridGroup.selectAll('.grid-line')
        .data(scale.ticks(5))
        .join('line')
        .attr('class', 'grid-line')
        .attr('x1', d => scale(d))
        .attr('x2', d => scale(d))
        .attr('y1', 0)
        .attr('y2', this.innerHeight)
        .attr('stroke', 'var(--color-border)')
        .attr('stroke-width', 1)
        .attr('stroke-dasharray', '4,4')
        .attr('opacity', 0.2);
    }
  }

  private addAxes(
    xScale: d3.ScaleBand<string> | d3.ScaleLinear<number, number>,
    yScale: d3.ScaleBand<string> | d3.ScaleLinear<number, number>,
    isVertical: boolean
  ): void {
    const xAxis = isVertical
      ? d3.axisBottom(xScale as d3.ScaleBand<string>)
      : d3.axisBottom(xScale as d3.ScaleLinear<number, number>).ticks(5);

    this.chartGroup.append('g')
      .attr('class', 'x-axis')
      .attr('transform', `translate(0,${this.innerHeight})`)
      .call(xAxis as any)
      .selectAll('text')
      .attr('fill', 'var(--color-text)')
      .attr('font-size', 'var(--font-size-sm)')
      .style('text-anchor', isVertical ? 'middle' : 'end');

    const yAxis = isVertical
      ? d3.axisLeft(yScale as d3.ScaleLinear<number, number>).ticks(5)
      : d3.axisLeft(yScale as d3.ScaleBand<string>);

    this.chartGroup.append('g')
      .attr('class', 'y-axis')
      .call(yAxis as any)
      .selectAll('text')
      .attr('fill', 'var(--color-text)')
      .attr('font-size', 'var(--font-size-sm)');

    this.chartGroup.selectAll('.domain')
      .attr('stroke', 'var(--color-border)')
      .attr('stroke-width', 2)
      .attr('opacity', 0.6);

    this.chartGroup.selectAll('.tick line')
      .attr('stroke', 'var(--color-border)')
      .attr('opacity', 0.5);

    if (isVertical) {
      const labels = this.data.map(d => String(d.label));
      const fontSize = 14;

      let rotationAngle = 0;
      if (this.labelRotationAngle !== undefined) {
        rotationAngle = this.labelRotationAngle;
      } else if (this.autoRotateLabels) {
        const maxWidth = getMaxLabelWidth(labels, fontSize);
        rotationAngle = calculateLabelRotation(this.innerWidth, labels, maxWidth);
      }

      const xAxisLabels = this.chartGroup.selectAll('.x-axis .tick text');

      if (rotationAngle === 0) {
        xAxisLabels
          .attr('transform', 'rotate(0)')
          .attr('dy', '1.2em')
          .attr('dx', '0em')
          .style('text-anchor', 'middle');
      } else if (rotationAngle === -45) {
        xAxisLabels
          .attr('transform', 'rotate(-45)')
          .attr('dy', '0.75em')
          .attr('dx', '-0.5em')
          .style('text-anchor', 'end');
      } else if (rotationAngle === -90) {
        xAxisLabels
          .attr('transform', 'rotate(-90)')
          .attr('dy', '-0.5em')
          .attr('dx', '-1.5em')
          .style('text-anchor', 'end');
      } else {
        xAxisLabels
          .attr('transform', `rotate(${rotationAngle})`)
          .attr('dy', '0.75em')
          .attr('dx', rotationAngle < 0 ? '-0.5em' : '0.5em')
          .style('text-anchor', rotationAngle < 0 ? 'end' : 'start');
      }
    }
  }

  private addBars(
    xScale: d3.ScaleBand<string> | d3.ScaleLinear<number, number>,
    yScale: d3.ScaleBand<string> | d3.ScaleLinear<number, number>,
    colorScale: d3.ScaleOrdinal<string, string>,
    isVertical: boolean,
    barRadius: number
  ): void {
    const barsGroup = this.chartGroup.append('g').attr('class', 'bars');

    const bars = barsGroup.selectAll('.bar-group')
      .data(this.data)
      .join('g')
      .attr('class', 'bar-group');

    if (isVertical) {
      const xBand = xScale as d3.ScaleBand<string>;
      const yLinear = yScale as d3.ScaleLinear<number, number>;

      bars.append('rect')
        .attr('class', 'bar-shadow')
        .attr('x', d => (xBand(d.label) || 0) + 2)
        .attr('y', d => yLinear(d.value) + 2)
        .attr('width', xBand.bandwidth())
        .attr('height', d => this.innerHeight - yLinear(d.value))
        .attr('fill', 'rgba(0, 0, 0, 0.1)')
        .attr('rx', barRadius)
        .attr('ry', barRadius);

      bars.append('rect')
        .attr('class', 'bar')
        .attr('x', d => xBand(d.label) || 0)
        .attr('y', this.innerHeight)
        .attr('width', xBand.bandwidth())
        .attr('height', 0)
        .attr('fill', d => d.color || colorScale(d.label))
        .attr('rx', barRadius)
        .attr('ry', barRadius)
        .style('filter', 'url(#bar-3d-shadow)')
        .transition()
        .duration(this.config.animated !== false ? 600 : 0)
        .delay((_, i) => i * 50)
        .attr('y', d => yLinear(d.value))
        .attr('height', d => this.innerHeight - yLinear(d.value));

      bars.append('rect')
        .attr('class', 'bar-glass')
        .attr('x', d => xBand(d.label) || 0)
        .attr('y', d => yLinear(d.value))
        .attr('width', xBand.bandwidth())
        .attr('height', d => this.innerHeight - yLinear(d.value))
        .attr('fill', 'url(#bar-glass-gradient)')
        .attr('rx', barRadius)
        .attr('ry', barRadius)
        .attr('pointer-events', 'none')
        .attr('opacity', 0)
        .transition()
        .duration(this.config.animated !== false ? 600 : 0)
        .delay((_, i) => i * 50)
        .attr('opacity', 1);

    } else {
      const xLinear = xScale as d3.ScaleLinear<number, number>;
      const yBand = yScale as d3.ScaleBand<string>;

      bars.append('rect')
        .attr('class', 'bar-shadow')
        .attr('x', 2)
        .attr('y', d => (yBand(d.label) || 0) + 2)
        .attr('width', d => xLinear(d.value))
        .attr('height', yBand.bandwidth())
        .attr('fill', 'rgba(0, 0, 0, 0.1)')
        .attr('rx', barRadius)
        .attr('ry', barRadius);

      bars.append('rect')
        .attr('class', 'bar')
        .attr('x', 0)
        .attr('y', d => yBand(d.label) || 0)
        .attr('width', 0)
        .attr('height', yBand.bandwidth())
        .attr('fill', d => d.color || colorScale(d.label))
        .attr('rx', barRadius)
        .attr('ry', barRadius)
        .style('filter', 'url(#bar-3d-shadow)')
        .transition()
        .duration(this.config.animated !== false ? 600 : 0)
        .delay((_, i) => i * 50)
        .attr('width', d => xLinear(d.value));

      bars.append('rect')
        .attr('class', 'bar-glass')
        .attr('x', 0)
        .attr('y', d => yBand(d.label) || 0)
        .attr('width', d => xLinear(d.value))
        .attr('height', yBand.bandwidth())
        .attr('fill', 'url(#bar-glass-gradient)')
        .attr('rx', barRadius)
        .attr('ry', barRadius)
        .attr('pointer-events', 'none')
        .attr('opacity', 0)
        .transition()
        .duration(this.config.animated !== false ? 600 : 0)
        .delay((_, i) => i * 50)
        .attr('opacity', 1);
    }

    bars.selectAll('.bar')
      .style('cursor', 'pointer')
      .on('mouseenter', (event, d: any) => {
        d3.select(event.currentTarget)
          .transition()
          .duration(300)
          .ease(d3.easeCubicOut)
          .attr('opacity', 0.9)
          .style('filter', 'url(#bar-hover-glow) url(#bar-3d-shadow)')
          .attr('transform', isVertical ? 'translateY(-5) scale(1.02)' : 'translateX(5) scale(1.02)');

        this.ngZone.run(() => {
          this.showTooltip(event, d, colorScale);
        });
      })
      .on('mousemove', (event) => {
        this.ngZone.run(() => {
          this.tooltipX = event.clientX;
          this.tooltipY = event.clientY;
        });
      })
      .on('mouseleave', (event) => {
        d3.select(event.currentTarget)
          .transition()
          .duration(300)
          .ease(d3.easeCubicOut)
          .attr('opacity', 1)
          .style('filter', 'url(#bar-3d-shadow)')
          .attr('transform', 'translate(0,0) scale(1)');

        this.ngZone.run(() => {
          this.hideTooltip();
        });
      });

    if (this.config.showValues) {
      bars.append('text')
        .attr('class', 'bar-value')
        .attr('x', d => isVertical ? ((xScale as d3.ScaleBand<string>)(d.label) || 0) + (xScale as d3.ScaleBand<string>).bandwidth() / 2 : (xScale as d3.ScaleLinear<number, number>)(d.value) + 5)
        .attr('y', d => isVertical ? (yScale as d3.ScaleLinear<number, number>)(d.value) - 5 : ((yScale as d3.ScaleBand<string>)(d.label) || 0) + (yScale as d3.ScaleBand<string>).bandwidth() / 2 + 5)
        .attr('text-anchor', isVertical ? 'middle' : 'start')
        .attr('fill', 'var(--color-text)')
        .attr('font-size', 'var(--font-size-sm)')
        .attr('font-weight', '600')
        .style('text-shadow', '0 1px 2px rgba(255, 255, 255, 0.8)')
        .attr('opacity', 0)
        .text(d => d.value.toLocaleString())
        .transition()
        .duration(this.config.animated !== false ? 600 : 0)
        .delay((_, i) => i * 50 + 300)
        .attr('opacity', 1);
    }
  }

  private showTooltip(event: MouseEvent, dataPoint: ChartDataPoint, colorScale: d3.ScaleOrdinal<string, string>): void {
    if (this.config.customTooltip) {
      const customData = this.config.customTooltip({
        label: dataPoint.label,
        value: dataPoint.value,
        color: dataPoint.color || colorScale(dataPoint.label)
      });
      this.tooltipTitle = customData.title || '';
      this.tooltipData = customData.items.map(item => ({
        label: item.label,
        value: item.value,
        color: item.color
      }));
    } else {
      this.tooltipTitle = dataPoint.label;
      this.tooltipData = [
        { label: 'Value', value: dataPoint.value.toLocaleString(), color: dataPoint.color || colorScale(dataPoint.label) }
      ];
    }

    this.tooltipX = event.clientX;
    this.tooltipY = event.clientY;
    this.tooltipVisible = true;
    this.cdr.detectChanges();
  }

  private hideTooltip(): void {
    this.tooltipVisible = false;
    this.cdr.detectChanges();
  }
}

